mapper.select:
SELECT A.bookmark_seq , A.bookmark_type , A.bookmark_category , A.bookmark_description , A.bookmark_url , A.reg_id , A.reg_name , A.reg_nickname , DATE(A.reg_date) reg_date , A.mod_id , A.mod_name , A.mod_nickname , DATE(A.mod_date) mod_date , A.del_yn , B.my_seq FROM bookmark A LEFT OUTER JOIN bookmark_my B ON A.bookmark_seq = B.bookmark_seq AND ((B.reg_id = #{reg_id} AND B.del_yn = 'N') OR A.reg_id = #{reg_id}) WHERE A.del_yn = 'N'
AND ${search_condition} LIKE CONCAT('%', #{search_word},'%')
ORDER BY bookmark_seq DESC LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO bookmark( bookmark_type, bookmark_category, bookmark_description, bookmark_url, reg_id, reg_name, reg_nickname, reg_date )
	 VALUES ( 
	 			#{bookmark_type}
	 		 	, #{bookmark_category}
	 		 	, #{bookmark_description}
	 		 	, #{bookmark_url}
	 		 	, #{reg_id}
	 		 	, #{reg_name}
	 		 	, #{reg_nickname}
	 		 	, now() 
	 	    )

mapper.update:
UPDATE bookmark
   SET del_yn = 'Y', mod_id=#{mod_id}, mod_name=#{mod_name}, mod_nickname=#{mod_nickname}, mod_date=now()
 WHERE bookmark_seq = #{bookmark_seq}

mapper.select:
		SELECT distinct(bookmark_type)
		  FROM bookmark
		 WHERE del_yn = 'N'
		 
      	 	AND bookmark_type = #{bookmark_type}
    	 
    	 
      	 	AND bookmark_category = #{bookmark_category}
    	 
    	 
      	 	AND reg_id = #{reg_id}
    	 
	  ORDER BY bookmark_type ASC;

		SELECT distinct(bookmark_category)
		  FROM bookmark
		 WHERE del_yn = 'N'
		 
      	 	AND bookmark_type = #{bookmark_type}
    	 
    	 
      	 	AND bookmark_category = #{bookmark_category}
    	 
    	 
      	 	AND reg_id = #{reg_id}
    	 
	  ORDER BY bookmark_category ASC;
