mapper.select:
SELECT COUNT(*)
FROM member
WHERE member_id = #{member_id}

SELECT COUNT(*)
FROM member
WHERE member_email = #{member_email}

mapper.insert:
		INSERT INTO member(member_id, member_pw, member_name, member_nickname, member_email, reg_date)
		VALUES ( 
     				#{member_id}
     				, #{member_pw}
     				, #{member_name}
     				, #{member_nickname}
     				, #{member_email}
     				, now() 
		     	)
