mapper.select:
SELECT member_id, member_name, member_nickname, member_email, reg_date, mod_date
  FROM member
 WHERE member_id = #{member_id}

mapper.update:
UPDATE member
   SET member_name = #{member_name}
   	   , member_nickname = #{member_nickname}
   	   , member_email = #{member_email}
   	   , mod_date = now()
 WHERE member_id = #{member_id}

UPDATE member
   SET member_pw = #{member_pw}
       , mod_date = now()
 WHERE member_id = #{member_id}

mapper.select:
SELECT COUNT(*)
  FROM member
 WHERE member_id = #{member_id} 
   AND member_pw = #{member_pw_org}

SELECT member_id , member_name , member_nickname , member_email , reg_date , mod_date , level , del_yn FROM member WHERE 1=1
AND ${search_condition} LIKE CONCAT('%', #{search_word},'%')
ORDER BY reg_date DESC LIMIT #{limit} OFFSET #{offset}
